import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../../styles/theme';

const MessageContainer = styled.section`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${theme.colors.background.primary};
`;

const FireworksBackground = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: 0;
  opacity: 0.3;

  &::before,
  &::after {
    content: '';
    position: absolute;
    width: 3px;
    height: 3px;
    background: ${theme.colors.primary.main};
    box-shadow:
      0 0 10px ${theme.colors.primary.main};
    animation: firework 6s ease-out infinite;
  }

  &::before {
    left: 25%;
    animation-delay: 0s;
  }

  &::after {
    right: 25%;
    animation-delay: 3s;
  }

  @keyframes firework {
    0% {
      bottom: 10%;
      opacity: 0;
      transform: scale(0);
    }
    15% {
      bottom: 60%;
      opacity: 0.8;
      transform: scale(1);
    }
    100% {
      bottom: 90%;
      opacity: 0;
      transform: scale(15);
      filter: blur(10px);
    }
  }
`;

const FireworkSpark = styled.div`
  position: absolute;
  pointer-events: none;
  opacity: 0.2;

  &:nth-child(1) {
    left: 30%;
    animation: spark 5s ease-out infinite 1s;
  }

  &:nth-child(2) {
    left: 50%;
    animation: spark 5s ease-out infinite 2.5s;
  }

  &:nth-child(3) {
    left: 70%;
    animation: spark 5s ease-out infinite 4s;
  }

  &::before {
    content: '✨';
    position: absolute;
    font-size: 1.5rem;
    opacity: 0;
    animation: sparkle 5s ease-out infinite;
  }

  @keyframes spark {
    0% {
      bottom: 30%;
      opacity: 0;
    }
    50% {
      bottom: 70%;
      opacity: 0.5;
    }
    100% {
      bottom: 90%;
      opacity: 0;
    }
  }

  @keyframes sparkle {
    0%, 100% {
      transform: scale(0) rotate(0deg);
      opacity: 0;
    }
    50% {
      transform: scale(1) rotate(180deg);
      opacity: 0.3;
    }
  }
`;

const ContentWrapper = styled.div`
  position: relative;
  z-index: 1;
  max-width: 1200px;
  width: 100%;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: bold;
  text-align: center;
  margin-bottom: 3rem;
  color: ${theme.colors.primary.main};
  text-shadow: ${theme.colors.glow.blue};
  font-family: ${theme.fonts.secondary};
  animation: twinkle 2s infinite;

  @media (max-width: ${theme.breakpoints.mobile}) {
    font-size: 2rem;
    margin-bottom: 2rem;
  }
`;

const MessageCard = styled(motion.div)`
  background: rgba(26, 26, 26, 0.9);
  border: 2px solid ${theme.colors.primary.main};
  border-radius: 12px;
  padding: 3rem;
  position: relative;
  overflow: hidden;
  backdrop-filter: blur(10px);
  box-shadow: 
    0 0 30px rgba(0, 255, 255, 0.3),
    inset 0 0 20px rgba(0, 255, 255, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, 
      ${theme.colors.primary.main}, 
      ${theme.colors.primary.dark},
      ${theme.colors.secondary.main},
      ${theme.colors.primary.main}
    );
    background-size: 400% 400%;
    z-index: -1;
    animation: gradient-border 10s ease infinite;
