interface Post {
  id: number;
  title: string;
  excerpt: string;
  content: string;
  featuredImage: string;
  link: string;
  date: string;
  author: string;
  categories: string[];
}

const BlogPost: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadPost = () => {
      if (!id) return;

      try {
        setLoading(true);
        setError(null);

        const postId = parseInt(id);
        if (isNaN(postId)) {
          setError('ÁÑ°Âäπ„Å™Ë®ò‰∫ãID„Åß„Åô');
          setLoading(false);
          return;
        }

        // posts.json„Åã„ÇâË®ò‰∫ã„ÇíÊ§úÁ¥¢
        const foundPost = postsData.posts.find((p: any) => p.id === postId);

        if (!foundPost) {
          setError('Ë®ò‰∫ã„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü');
          setLoading(false);
          return;
        }

        setPost(foundPost);

      } catch (err) {
        console.error('Error loading post:', err);
        setError('Ë®ò‰∫ã„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      } finally {
        setLoading(false);
      }
    };

    loadPost();
  }, [id]);

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ja-JP', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // „Ç≥„É≥„ÉÜ„É≥„ÉÑÂÜÖ„ÅÆÁîªÂÉèURL„ÇíÂ§âÊèõ
  const convertContentImages = (content: string): string => {
    if (!content) return '';

    // WordPress„ÅÆÁîªÂÉèURL„ÇíGitHub PagesÁî®„Å´Â§âÊèõ
    let convertedContent = content.replace(
      /https?:\/\/[^\/]+\/wp-content\/uploads\/(\d{4})\/(\d{2})\/([^"'\s]+)/g,
      (match, year, month, filename) => getAssetPath(`${year}/${month}/${filename}`)
    );

    // Áõ∏ÂØæ„Éë„Çπ„ÅÆÁîªÂÉè„ÇÇÂ§âÊèõ
    convertedContent = convertedContent.replace(
      /src="\/(\d{4})\/(\d{2})\/([^"]+)"/g,
      (match, year, month, filename) => `src="${getAssetPath(`${year}/${month}/${filename}`)}"`
    );

    return convertedContent;
  };

  if (loading) {
    return (
      <BlogPostContainer>
        <ContentWrapper>
          <LoadingContainer>
            <LoadingSpinner />
            <p style={{ marginTop: '1rem' }}>Ë®ò‰∫ã„ÇíË™≠„ÅøËæº„Åø‰∏≠...</p>
          </LoadingContainer>
        </ContentWrapper>
      </BlogPostContainer>
    );
  }

  if (error || !post) {
    return (
      <BlogPostContainer>
        <ContentWrapper>
          <ErrorContainer>
            <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>„Ç®„É©„Éº</h2>
            <p>{error || 'Ë®ò‰∫ã„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü'}</p>
            <ErrorButton
              onClick={() => navigate(-1)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Êàª„Çã
            </ErrorButton>
          </ErrorContainer>
        </ContentWrapper>
      </BlogPostContainer>
    );
  }

  return (
    <BlogPostContainer>
      <ContentWrapper>
        <BackLinks>
          <BackLink to="/">‚Üê „Éõ„Éº„É†„Å´Êàª„Çã</BackLink>
          <BackLink to="/blog">‚Üê Ë®ò‰∫ã‰∏ÄË¶ß„Å´Êàª„Çã</BackLink>
        </BackLinks>

        <Article
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Header>
            <Title>{post.title}</Title>
            <Meta>
              <MetaItem>üìÖ {formatDate(post.date)}</MetaItem>
              <MetaItem>‚úçÔ∏è {post.author}</MetaItem>
            </Meta>
            {post.categories && post.categories.length > 0 && (
              <Categories>
                {post.categories.map((category, index) => (
                  <Category key={index}>{category}</Category>
                ))}
              </Categories>
            )}
          </Header>

          {post.featuredImage && (
            <FeaturedImage>
              <img 
                src={getAssetPath(post.featuredImage.replace(/^\//, ''))} 
                alt={post.title}
                onError={(e) => {
                  e.currentTarget.style.display = 'none';
                }}
              />
            </FeaturedImage>
          )}

          <Content>
            {post.excerpt && (
              <Excerpt>
                <p>{post.excerpt.substring(0, 200)}...</p>
              </Excerpt>
            )}

            <Body dangerouslySetInnerHTML={{ __html: convertContentImages(post.content) }} />
          </Content>
        </Article>
      </ContentWrapper>
    </BlogPostContainer>
  );
};

export default BlogPost;
